import{_ as e,c as n,o as i,a4 as a}from"./chunks/framework.DpC1ZpOZ.js";const f=JSON.parse('{"title":"Allgemeine Vorgehensweise beim Refactoring","description":"","frontmatter":{"layout":"doc"},"headers":[],"relativePath":"de-DE/5.refactoring/general.md","filePath":"de-DE/5.refactoring/general.md"}'),r={name:"de-DE/5.refactoring/general.md"},t=a('<h1 id="allgemeine-vorgehensweise-beim-refactoring" tabindex="-1">Allgemeine Vorgehensweise beim Refactoring <a class="header-anchor" href="#allgemeine-vorgehensweise-beim-refactoring" aria-label="Permalink to &quot;Allgemeine Vorgehensweise beim Refactoring&quot;">​</a></h1><h2 id="kommunikation" tabindex="-1">Kommunikation <a class="header-anchor" href="#kommunikation" aria-label="Permalink to &quot;Kommunikation&quot;">​</a></h2><p>Code-Reviews funktionieren nur, wenn die Kommunikation zwischen den Teammitgliedern stimmt. Menschen machen Fehler und es ist wichtig, dass diese Fehler offen angesprochen werden können. Code-Reviews helfen daher nur, wenn sie in einer offenen und respektvollen Atmosphäre stattfinden und gute Code als gemeinsames Teamziel gesehen wird.</p><h2 id="zeitpunkt" tabindex="-1">Zeitpunkt <a class="header-anchor" href="#zeitpunkt" aria-label="Permalink to &quot;Zeitpunkt&quot;">​</a></h2><p>Code-Reviews sollen nicht erst am Ende eines Entwicklungsprozesses stattfinden, sondern bereits, wenn ein Teil des Codes geschrieben ist. Auf diese Weise können Reviews dazu beitragen, dass der Code bereits während der Entstehung verbessert wird und nicht erst am Ende, wenn der Großteil des Codes bereits geschrieben ist.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Insbesondere am Anfang eines Projektes ist es normal, dass viele Code-Reviews sehr groß und umfangreich sind. Das Team ist nich nicht aufeinander eingespielt und viele Kodierregele sind noch nicht etabliert. Daher ist es wichtig, dass nicht nur Code-Reviews zeitnah, sondern auch Pair-Programming und eine <em>gesunde</em> Kommunikation im Team stattfindet.</p></div><h2 id="personliche-einstellung" tabindex="-1">Persönliche Einstellung <a class="header-anchor" href="#personliche-einstellung" aria-label="Permalink to &quot;Persönliche Einstellung&quot;">​</a></h2><p>Code-Reviews sollen nicht als Kritik an der eigenen Person, sondern als Verbesserungsvorschläge für den Code gesehen werden. Auf der anderen Seite ist es daher unangebracht persönliche Angriffe gegenüber dem Code-Reviewenden zu machen.</p><blockquote><p>Sollte ein Code-Review zu persönlich werden, sollte dies sofort angesprochen werden. Zu viele Korrektur-Kommentare deuten darauf hin, dass das Ziel des Reviewers und des Reviewten nicht übereinstimmen.</p></blockquote><h2 id="umbenennen" tabindex="-1">Umbenennen <a class="header-anchor" href="#umbenennen" aria-label="Permalink to &quot;Umbenennen&quot;">​</a></h2><h2 id="vereinfachungen" tabindex="-1">Vereinfachungen <a class="header-anchor" href="#vereinfachungen" aria-label="Permalink to &quot;Vereinfachungen&quot;">​</a></h2><h2 id="verantwortlichkeiten-trennen" tabindex="-1">Verantwortlichkeiten trennen <a class="header-anchor" href="#verantwortlichkeiten-trennen" aria-label="Permalink to &quot;Verantwortlichkeiten trennen&quot;">​</a></h2><h2 id="methoden-extrahieren" tabindex="-1">Methoden extrahieren <a class="header-anchor" href="#methoden-extrahieren" aria-label="Permalink to &quot;Methoden extrahieren&quot;">​</a></h2><h2 id="benamung" tabindex="-1">Benamung <a class="header-anchor" href="#benamung" aria-label="Permalink to &quot;Benamung&quot;">​</a></h2>',14),s=[t];function o(d,h,l,c,u,m){return i(),n("div",null,s)}const b=e(r,[["render",o]]);export{f as __pageData,b as default};
